const Post = props => (
  <Layout>
    <h1>{props.posts[0].title}</h1>
    <ul>
      {props.posts
        .filter(post => post.id === 14)
        .map(post => (
          <li key={post.id}>
            <a>{post.title}</a>
            <p>{post.body}</p>
          </li>
        ))}
    </ul>
  </Layout>
);

Post.getInitialProps = function() {
  return Fetcher.get();
};

export default Post;
