import React, { useState } from "react";
import Layout from "../../components/MyLayout.js";
import * as Fetcher from "../../helpers/fetcher";

// function handleSubmit(event) {
//   event.preventDefault();
//   console.log(event.target);
// }

// function handleChange(event) {
//   console.log(event.target.value);
// }

const New = function(props) {
  const [newPost, setNewPost] = useState({ title: "", body: "" });

  // console.log(props.posts);

  function handleSubmit(event) {
    event.preventDefault();

    New.getInitialProps = function() {
      return Fetcher.post();
    };

    console.log(props.posts);

    setNewPost({ title: "", body: "" });
  }

  function handleChange(event) {
    // console.log(event.target.name);

    if (event.target.name === "title") {
      setNewPost({ title: event.target.value, body: newPost.body });
    } else if (event.target.name === "body") {
      setNewPost({ title: newPost.title, body: event.target.value });
    }
  }

  // console.log(newPost);

  return (
    <Layout>
      <form onSubmit={handleSubmit}>
        <label>
          Title
          <input
            type="text"
            name="title"
            value={newPost.title}
            onChange={handleChange}
          />
        </label>
        <label>
          Post
          <textarea name="body" value={newPost.body} onChange={handleChange} />
        </label>
        <input type="submit" value="Send" />
      </form>
      {/* {props.posts
          .filter(posts => posts.id === +router.query.id)
          .map(post => (
            <li key={post.id}>
              <a>{post.title}</a>
              <p>{post.body}</p>
            </li>
          ))} */}
    </Layout>
  );
};

// New.getInitialProps = function() {
//   return Fetcher.post();
// };

export default New;
