//     </Layout>
//   );
// }

const Post = props => (
  <Layout>
    <h1>ABC</h1>
    <ul>
      {props.posts.filter(post =>
        post.id === props.router.query.id ? (
          <li key={post.id}>
            <Link href="/posts/[id]" as={`/posts/${post.id}`}>
              <a>{post.title}</a>
            </Link>
            <p>{post.body}</p>
          </li>
        ) : (
          <h1>NO</h1>
        )
      )}
    </ul>
  </Layout>
);

Post.getInitialProps = function() {
  return Fetcher.get();
};

export default Post;
