import React from 'react';
import { NextPage, NextPageContext } from 'next';
import { Layout } from '../components/MyLayout';
import Link from 'next/link';
import * as Fetcher from '../helpers/fetcher';
import styled from 'styled-components';

const Title = styled.h1`
    font-family: roboto;
    text-align: center;
    display: block;
    margin-top: 50px;
`;

const LinkStyle = styled.a`
    color: blue;
    padding-right: 10px;
    padding-left: 10px;
    cursor: pointer;

    &:hover {
        color: red;
    }
`;

const StyledWrapperUL = styled.ul`
    text-align: center;
    list-style-type: none;
    padding: 0;
`;

const StyledWrapperLI = styled.li`
    text-align: center;
    border-radius: 5px;
    margin: 15px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
    width: 50%;
`;

const Index: NextPage<Context> = props => {
    return (
        <Layout>
            <Title>Latest Posts</Title>
            <StyledWrapperUL>
                {props.posts.map(post => (
                    <StyledWrapperLI key={post.id}>
                        <Link href="/posts/[id]" as={`/posts/${post.id}`}>
                            <LinkStyle>{post.title}</LinkStyle>
                        </Link>
                        <p>{post.body}</p>
                    </StyledWrapperLI>
                ))}
            </StyledWrapperUL>
        </Layout>
    );
};

interface Context extends NextPageContext {
    posts: Array<{ [key: string]: string | number }>;
}

Index.getInitialProps = async Context => {
    return Fetcher.get();
};

export default Index;
