const axios = require("axios");

export function get() {
  return axios
    .get(`https://simple-blog-api.crew.red/posts`)
    .then(function(response) {
      return {
        posts: response.data
      };
    })
    .catch(function(error) {
      console.log(error);
    });
}

export function post() {
  return axios
    .post(`https://simple-blog-api.crew.red/posts`, {})
    .then(function(response) {
      return {
        posts: response.data
      };
    })
    .catch(function(error) {
      console.log(error);
    });
}

axios
  .post("/user", {
    firstName: "Fred",
    lastName: "Flintstone"
  })
  .then(function(response) {
    console.log(response);
  })
  .catch(function(error) {
    console.log(error);
  });
