const axios = require("axios");

export function get() {
  return axios
    .get(`https://simple-blog-api.crew.red/posts`)
    .then(function(response) {
      return {
        posts: response.data
      };
    })
    .catch(function(error) {
      console.log(error);
    });
}

export function post() {
  // console.log(event.target.value);
  return axios
    .post(`https://simple-blog-api.crew.red/posts`, {
      title: "555",
      body: "555"
    })
    .then(function(response) {
      return {
        posts: response.data
      };
    })
    .catch(function(error) {
      console.log(error);
    });
}

