const axios = require("axios");

export function get(id = "1") {
  // console.log(`https://simple-blog-api.crew.red/posts${"/" + id}`);
  return axios
    .get(`https://simple-blog-api.crew.red/posts${"/" + id}`)
    .then(function(response) {
      return {
        posts: response.data
      };
    })
    .catch(function(error) {
      console.log(error);
    });
}
