import { useRouter } from "next/router";
import Layout from "../../components/MyLayout";
import * as Fetcher from "../../helpers/fetcher";

const Post = function(props) {
  const router = useRouter();
  return (
    <Layout>
      <ul>
        {props.posts
          .filter(post => post.id === +router.query.id)
          .map(post => (
            <li key={post.id}>
              <a>{post.title}</a>
              <p>{post.body}</p>
            </li>
          ))}
      </ul>
    </Layout>
  );
};

Post.getInitialProps = function() {
  return Fetcher.get();
};

export default Post;
