const Post = function(props) {
  const router = useRouter();
  console.log(router);
  return (
    <Layout>
      <ul>
        {props.posts
          .filter(posts => posts.id === 14)
          .map(post => (
            <li key={post.id}>
              <a>{post.title}</a>
              <p>{post.body}</p>
            </li>
          ))}
      </ul>
    </Layout>
  );
};

Post.getInitialProps = function() {
  return Fetcher.get();
};

export default Post;
